# this works only for digits that have equal length


def sum_with_nagative(num1, num2):
    new_num1 = ''
    new_num2 = ''
    # if num1.__contains__('-') and num2.__contains__('-'):

    if num1.__contains__('-') or num2.__contains__('-'):
        new_num1 = num1.replace('-', '')
        new_num2 = num2.replace('-', '')

        if int(new_num1) < int(new_num2):
            temp = new_num1
            new_num1 = new_num2
            new_num2 = temp
    else:
        new_num1 = num1
        new_num2 = num2

    reverse_num1 = new_num1[len(new_num1)::-1]
    reverse_num2 = new_num2[len(new_num2)::-1]

    list = []
    dif = 0
    carry = 0
    for i in range(len(reverse_num1)):
        dif = int(reverse_num1[i]) - int(reverse_num2[i]) - carry
        if dif >= 0:
            carry = 0
            list.append(dif)
        elif dif < 0:
            dif = dif+10
            carry = 1
            list.append(dif)

    list.reverse()
    str_ints = [str(int) for int in list]

    return ("".join(str_ints))


def main():
    print(sum_with_nagative("-1234", "9876"))
    print(sum_with_nagative("-1983", "9876"))
    print(sum_with_nagative("1234", "-9876"))
    print(sum_with_nagative("9876", "-1234"))
    input1 = ""
    input2 = ""

    print(sum_with_nagative(input1, input2))


# sum_with_nagative("-9876", "-1234")

main()
